def new_split_iter( expr ):   expr = expr + ";"                  pos = 0                                while expr[pos] != ";":                     if expr[pos] == " ":         pos += 1      elif expr[pos] == ">" or expr[pos] == "<" or expr[pos] == "!" or expr[pos] == "=":         num = expr[pos]         pos += 1         if expr[pos] == "=":            num = num + expr[pos]            pos += 1         yield num                elif expr[pos].isalnum():         num = expr[pos]         pos += 1         while expr[pos].isdigit() or expr[pos].isalnum() or expr[pos] == ".":            num = num + expr[pos]            pos += 1         yield num      elif expr[pos] != " " or expr[pos] != ";":         yield expr[pos]         pos += 1      elif expr[pos] == "%":         yield expr[pos]         pos += 1      else:         pos += 1            yield ";"print (list( new_split_iter( "3+4 >=5+4/2" )))